INSERT INTO `Page` (`PageId`, `Title`, `Content`, `UserId`) VALUES
(1, 'Contact Us', '<h3>Because we receive an extremely high volume of email, before you email us please check your brain&nbsp;for answers to your questions.</h3>\r\n<p><img style="display: block; margin-left: auto; margin-right: auto;" src="https://media.licdn.com/mpr/mpr/AAEAAQAAAAAAAAUbAAAAJDI5NDBjNGI1LWIwZDUtNDU2Ni1iMDkwLWI2ZGJiMjY2NGIzYg.jpg" alt="" width="698" height="400" /></p>', 1),
(2, 'About Us', '<p>Catalyst Athletics is a <a href="http://www.catalystathletics.com/weightlifting-team/">USA Weightlifting National Champion team</a> and is the world\'s largest source of educational material for Olympic weightlifting. <br /><br />We publish online content from <a href="http://www.catalystathletics.com/articles/">articles</a> to <a href="http://www.catalystathletics.com/media/video/">videos</a> and <a href="http://www.catalystathletics.com/media/photo/">photos</a> to <a href="http://www.catalystathletics.com/olympic-weightlifting-workouts/">Olympic weightlifting training programs</a> and the largest online <a href="http://www.catalystathletics.com/exercises/">weightlifting exercise library</a>; <a href="http://www.catalystathletics.com/store/books">books</a> in print and digital format; <a href="http://www.catalystathletics.com/store/video-dvds">video</a> in DVD, downloadable and streaming format; a <a href="http://www.performancemenu.com/">monthly journal</a>; and produced the first feature-length <a href="http://www.americanweightliftingfilm.com/">documentary film on the sport of weightlifting</a> in the US. Additionally, we provide <a href="http://www.catalystathletics.com/olympic-weightlifting-seminars/">seminars</a> and <a href="http://www.catalystathletics.com/olympic-weightlifting-seminars/certification/">coaching certification</a> both in the US and internationally. <br /><br />Catalyst Athletics was founded in 2006 by Greg Everett and merged with <a href="http://www.performancemenu.com/">The Performance Menu journal</a>, whose first issue was released in February 2005. Everett is the author of the world\'s most popular book on <a href="http://www.catalystathletics.com/olympic-weightlifting/">Olympic weightlifting</a>, <a href="https://shop.catalystathletics.com/collections/books/products/olympic-weightlifting-a-complete-guide-for-athletes-coaches"><em>Olympic Weightlifting: A Complete Guide for Athletes &amp; Coaches</em></a> among several other <a href="http://www.catalystathletics.com/store/books">books</a>; co-founder and editor-in-chief of the <a href="http://www.performancemenu.com/">Performance Menu journal</a>; writer, producer, director and editor of <a href="http://www.americanweightliftingfilm.com/">American Weightlifting: The Documentary</a>; creator of most of the website\'s content; developer of the Catalyst Athletics websites; head coach of the national champions <a href="http://www.catalystathletics.com/weightlifting-team/">Catalyst Athletics weightlifting team</a>; a national-level competitive weightlifter, masters national champion, and masters American record holder in the clean &amp; jerk. <br /><br />We <a href="http://www.catalystathletics.com/article/1987/Major-Changes-at-Catalyst-Athletics/">relocated in 2016</a> from Sunnyvale, California to central Oregon, closing our commercial gym business and transitioning into a private training facility exclusively for our weightlifting team. <br /><br />Our <a href="http://www.catalystathletics.com/olympic-weightlifting-workouts/training-programs/">training programs</a> and methodology are used by athletes, coaches and gyms around the world, and we have a growing network of <a href="http://www.catalystathletics.com/affiliate-gyms/">affiliate gyms</a> internationally.</p>\r\n<h4>COMPANY MISSION</h4>\r\n<p>To produce and make as widely accessible as possible educational material in practical formats for weightlifters and coaches to help them develop maximally. <br /><br />To help the growth of the sport of weightlifting and improve the quality of lifting and coaching in the US.</p>\r\n<h4>TEAM MISSION</h4>\r\n<p>To coach and develop weightlifters to the highest levels of national and international competition as part of a true team&mdash;not pay for mercenaries to represent a brand as a marketing tactic. <br /><br />To preserve a way of life and a mindset unique to the roots of the sport and in particular to the American history of the sport. <br /><br />To win individual and team medals in national and international competition. <br /><br />To break American records. <br /><br />To win team national championships. <br /><br /><br /><img src="http://www.catalystathletics.com/images/weightliftingteam/2016Team.jpg" /><br />2016 USA Weightlifting Women\'s <a href="http://www.catalystathletics.com/weightlifting-team/">Team National Champions</a> <br /><br /><img src="http://www.catalystathletics.com/articles/images/2016-03-31-gymNews.jpg" /><br />The Sunnyvale gym that was <a href="http://www.catalystathletics.com/article/1987/Major-Changes-at-Catalyst-Athletics/">closed in 2016</a> <br /><br /><img src="http://www.catalystathletics.com/images/about/about-oregonGym.jpg" /><br />The current Oregon gym </p>', 1),
(3, 'Newsletter', '<h2 class="newsletter_page">GET OUR OLYMPIC WEIGHTLIFTING&nbsp;MANUAL FREE</h2>\r\n<p>Subscribe to our newsletter and receive emails with training tips from our team, news updates, events info, exclusive sales and more!</p>\r\n<p><img style="display: block; margin-left: auto; margin-right: auto;" src="http://cdn-maf3.heartyhosting.com/sites/muscleandfitness.com/files/u17/the_snatch.jpg" alt="" width="666" height="600" /></p>', 2);
